/**
 * 将 <div class="... bubble message-bubble ...">内容</div> 转换成
 * <bubble class="... 其他class ...">内容</bubble>
 * 并自动去除 class 中的 bubble 和 message-bubble（如果存在）
 * 只处理最外层的 div，即使内容里有嵌套 div 也不会被修改
 * 支持 class 使用单引号或双引号，支持属性间无空格（如 <div class="a"id="b">）
 */
function divToBubble(str) {
  // 如果字符串不是以 <div 开头，直接返回原字符串
  if (!/^<div\b/i.test(str)) {
    return str;
  }
  return str.replace(/^<div\b([^>]*)>([\s\S]*)(<\/div\s*>)$/i, (match, attrs, content) => {
    let newAttrs = attrs;
    // 匹配 class 属性（支持单/双引号，支持 = 前后有任意空格）
    const classMatch = attrs.match(/class\s*=\s*(["'])([^"']*)\1/i);
    if (classMatch) {
      const classes = classMatch[2].split(/\s+/).filter(Boolean);
      const filtered = classes.filter(cls => cls !== 'bubble' && cls !== 'message-bubble');
      const newClassValue = filtered.join(' ');
      // 保留原来的引号类型，并尽量保持 = 前有空格
      const newClassAttr = newClassValue 
        ? ` class=${classMatch[1]}${newClassValue}${classMatch[1]}` 
        : '';
      // 用正则精确替换原来的整个 class 属性部分
      newAttrs = attrs.replace(classMatch[0], newClassAttr);
    }
    // 清理多余空格（属性之间保留单个空格）
    newAttrs = newAttrs.replace(/\s+/g, ' ').trim();
    // 如果还有其他属性，前面加一个空格
    const attrStr = newAttrs ? ` ${newAttrs}` : '';
    return `&lt;bubble${attrStr}&gt;${content}&lt;/bubble&gt;`;
  });
}

function revealOuterHTML(event, target, type) {
    event.stopPropagation();
    if (type === "link") {
        const to = target.getAttribute('to');
        target.innerHTML = "&lt;link to=\"" + to + "\"&gt;" + target.innerHTML + "&lt;/link&gt;";
    } else if (type === "bubble") {
        target.innerHTML = divToBubble(target.getAttribute("outerHtml"));
        target.querySelectorAll(".message-bubble[onclick^='revealOuterHTML']").forEach(bb => { bb.click() });
    }
    target.classList.add(`${type}-og-text`);
    target.setAttribute("onclick", `hideOuterHTML(event,this,"${type}")`);
}

function hideOuterHTML(event, target, type) {
    event.stopPropagation();
    target.classList.remove(`${type}-og-text`);
    target.innerHTML = target.getAttribute('innerHTML');
    target.setAttribute("onclick", `revealOuterHTML(event,this,"${type}")`);
}

function decodeBase64(text) {
    return new TextDecoder().decode(Uint8Array.from(atob(text), (c) => c.charCodeAt(0)));
}

function decode(fragment) {
    if (fragment.startsWith("@utf(\"") && fragment.endsWith("\");")) {
        fragment = fragment.replaceAll("&lt;", "<");
        fragment = fragment.substr("@utf(\"".length, fragment.length - "@utf(\"".length - "\");".length);
        const segments = [];
        let tempZh = "", tempEn = "", isInZh = false, isInEn = false;
        for (let i = 0; i < fragment.length; ++i) {
            const ch = fragment[i];
            if (ch === "<") {
                if (isInZh) {
                    segments.push({
                        text: tempZh,
                        isZh: true
                    });
                    tempZh = "";
                }
                isInEn = true;
                isInZh = false;
                tempEn = "";
            } else if (ch === ">") {
                if (isInEn) {
                    segments.push({
                        text: tempEn,
                        isEn: true
                    });
                    tempEn = "";
                }
                isInEn = false;
                isInZh = true;
                tempZh = "";
            } else {
                if (!isInEn && !isInZh) {
                    isInZh = true;
                    tempZh = ch;
                } else if (isInZh) {
                    tempZh += ch;
                } else if (isInEn) {
                    tempEn += ch;
                }
            }
        }
        if (tempZh.length) {
            segments.push({
                text: tempZh,
                isZh: true
            });
        }
        let result = "";
        for (const segment of segments) {
            if (segment.isEn) {
                result += segment.text;
            } else if (segment.isZh) {
                result += (function (segment) {
                    const encodedZh = segment.text;
                    let handle = "";
                    for (let i = 0; i < encodedZh.length; ++i) {
                        if (i % 2 === 0) {
                            handle += "%";
                        }
                        handle += encodedZh[i];
                    }
                    let decodedZh = "";
                    try {
                        decodedZh = decodeURIComponent(handle);
                    } catch {
                        return encodedZh;
                    }
                    return decodedZh;
                })(segment);
            }
        }
        return result;
    } else if (fragment.startsWith("@base64(\"") && fragment.endsWith("\");")) {
        fragment = fragment.substr("@base64(\"".length, fragment.length - "@base64(\"".length - "\");".length);
        return decodeBase64(fragment);
    }
    return fragment;
}

function getDecodeUrl() {
    if (window.location.hostname === "localhost") {
        let breadcrumbs = '/timeline-2025/timeline-2025-public/'.split("/");
        breadcrumbs.pop();
        return 'https://yhnnd.github.io' + breadcrumbs.join("/") + "/decode.html";
    }
    let breadcrumbs = window.location.pathname.split("/");
    breadcrumbs.pop();
    return window.location.origin + breadcrumbs.join("/") + "/decode.html";
}

const decodeUrl = getDecodeUrl() + "?content=";
const defaultDecodeUrl = "https://yhnnd.github.io/timeline-2024/timeline-2024-public/decode.html?content=";
const decodeBase64Url = getDecodeUrl() + "?base64=";
const defaultDecodeBase64Url = "https://yhnnd.github.io/timeline-2024/timeline-2024-public/decode.html?base64=";

function decrypt(value) {
    let temp = value;
    let encodeType = ""; // utf, base64
    if (temp.startsWith(decodeUrl)) {
        temp = temp.substr(decodeUrl.length);
        encodeType = "utf";
    } else if (temp.startsWith(defaultDecodeUrl)) {
        temp = temp.substr(defaultDecodeUrl.length);
        encodeType = "utf";
    } else if (temp.startsWith(decodeBase64Url)) {
        temp = temp.substr(decodeBase64Url.length);
        encodeType = "base64";
    } else if (temp.startsWith(defaultDecodeBase64Url)) {
        temp = temp.substr(defaultDecodeBase64Url.length);
        encodeType = "base64";
    }
    if (temp.includes("&")) {
        temp = temp.split("&")[0];
    }
    if (encodeType === "utf") {
        temp = temp.replaceAll("%3C", "<");
        temp = temp.replaceAll("%3E", ">");
        temp = temp.replaceAll("%0G", "(");
        temp = temp.replaceAll("%0H", ")");
        return temp.split("%0A").map(decodeOnline).join("\n");
    } else if (encodeType === "base64") {
        temp = temp.replaceAll("%3D", "=");
        return temp.split("%0A").map(decodeBase64).join("\n");
    }
}

