function _highlightSearchKeywords_(container) {
    highlightedKeywords = []
    const popup = document.querySelector(".popup");
    popup.setAttribute("disabled", "");
    const q = window.sessionStorage.getItem("q"), conf = window.sessionStorage.getItem("conf");
    if (!q) {
        return;
    }
    const searchKeywords = JSON.parse(q), configs = conf ? JSON.parse(conf) : null;
    if (!configs || configs.type === "text") {
        container.childNodes.forEach(pre => {
            if (pre.tagName === "PRE" && !pre.classList.contains("badges")) {
                traverse(pre, {
                    textHandler: highlightSearchKeywords,
                    textHandlerParams: {searchKeywords, configs}
                });
            }
        });
        if (!highlightedKeywords.length) {
            return;
        }
        const markerId = highlightedKeywords.shift();
        const marker = document.getElementById(markerId);
        if (!marker) {
            return;
        }
        const matched = popup?.querySelector("[matched-marker]");
        let gotoBtn = popup?.querySelector("[goto-matched]");
        if (!matched || !gotoBtn) {
            return;
        }
        matched.innerHTML = marker.outerHTML.split(`id="${markerId}"`).join("");
        if (highlightedKeywords.length){
            const ad = document.createElement("span");
            ad.style.color = "var(--studio-yellow-30)";
            ad.style.fontStyle = "italic";
            ad.innerHTML = `&nbsp;&nbsp;and ${highlightedKeywords.length} more`;
            matched.append(ad);
        }
        let copy = gotoBtn.cloneNode(true);
        gotoBtn = (gotoBtn.replaceWith(copy), copy); // Remove All Event Listeners
        gotoBtn.setAttribute("href", "#" + markerId);
        gotoBtn.addEventListener('click', (event) => {
            event.preventDefault();
            marker.scrollIntoView({behavior: 'instant', block: 'center'});
        });
        popup.removeAttribute("disabled");
        if (sessionStorage.getItem("popup-is-closed-by-user") !== "true") {
            popup.classList.add("opened");
        }
    }
}

function traverse(node, {textHandler, textHandlerParams}) {
    let child = node.firstChild;
    while (child) {
        const next = child.nextSibling; // 预取下一个，避免修改后丢失
        if (!child.isNewTextNode) {
            if (child.nodeType === Node.TEXT_NODE) {
                textHandler(child, textHandlerParams);
            } else {
                traverse(child, {textHandler, textHandlerParams});
            }
        }
        child = next;
    }
}

let highlightedKeywords = []

function highlightSearchKeywords(child, {searchKeywords, configs}) {
    if (child.isNewTextNode || !child.parentElement || child.parentElement.classList.contains('marker-wrapper')) {
        return;  // 跳过新文本节点
    }
    if (child.nodeType === Node.TEXT_NODE && child.textContent) {
        const newChildren = document.createDocumentFragment();
        const escapedKeywords = searchKeywords.map(key => key.replace(/[.*+?^${}()|[\]\\]/g, "\\$&")).sort((a, b) => b.length - a.length);
        const pattern = "(" + escapedKeywords.join("|") + ")";
        const regex1 = new RegExp(pattern, "gi");  // g: Global, i: Ignore case
        const regex2 = new RegExp("^(" + escapedKeywords.join("|") + ")$", "i");
        const textSplited = child.textContent.split(regex1);
        for (const text of textSplited) {
            if (!text) {
                continue;
            }
            let newTextNode;
            if (regex2.test(text)) {
                newTextNode = document.createElement("code");
                newTextNode.classList.add("marker-wrapper");
                newTextNode.innerHTML = "<var class=\"marker\">" + text + "</var>";
                newTextNode.id = "marker-" + Math.random().toString(36).substring(2);
                highlightedKeywords.push(newTextNode.id);
            } else {
                newTextNode = document.createTextNode(text);
            }
            newTextNode.isNewTextNode = true; // 标记新文本节点
            newChildren.appendChild(newTextNode);
        }
        child.parentElement.replaceChild(newChildren, child);
    }
}

function trimArticle({ogResponseText, t0, t1, cmd}) {
    let responseText;
    if (t0) {
        const startIndex = ogResponseText.indexOf(`@LineUniqueId("${t0}");\n`);
        if (startIndex >= 0) {
            const prevCmds = cmd ? ogResponseText.substring(0, startIndex).split("\n").filter(line => {return line.startsWith("@command(")}).join("\n") : "";
            responseText = prevCmds + ogResponseText.substring(startIndex + `@LineUniqueId("${t0}");\n`.length);
        }
    }
    if (t1) {
        const endIndex = responseText.indexOf(`@LineUniqueId("${t1}");`);
        if (endIndex >= 0) {
            const cmdsAfter = cmd ? responseText.substring(endIndex).split("\n").filter(line => {return line.startsWith("@command(")}).join("\n") : "";
            responseText = responseText.substring(0, endIndex) + cmdsAfter;
        }
    }
    return responseText;
}

function renderArticle(src, container1ClassName, container2ClassName) {
    ajax(src, undefined, window.localStorage.getItem("enable-cache") === "true", function(ogResponseText) {
        if (!window.currentArticle) {
            return;
        }
        window.currentArticle.ogResponseText = ogResponseText;
        if (window.currentArticle.isTrim) {
            const t0 = window.currentArticle.t0;
            const t1 = window.currentArticle.t1;
            window.currentArticle.responseText = trimArticle({ogResponseText, t0, t1, cmd: true});
        } else {
            window.currentArticle.responseText = ogResponseText;
        }
        renderArticleParse(window.currentArticle.responseText, container1ClassName, container2ClassName);
    });
}

function openFile(src) {
    if (!src || !src.length) {
        return;
    }
    if (src.endsWith(".html")) {
        document.querySelector(".desktop").style.display = "none";
        document.querySelector(".desktop-2").style.display = null;
        document.querySelector(".desktop-2 iframe").setAttribute("src", src);
        hideTimelineLoading();
        const footer = document.querySelector(".footer");
        if (footer) {
            footer.style.display = "none";
        }
    } else {
        document.querySelector(".desktop-2").style.display = "none";
        window.currentArticle.isTrim = ["true", "1"].includes(getParameter("trm"));
        window.currentArticle.t0 = getParameter("t0");
        window.currentArticle.t1 = getParameter("t1");
        renderArticle(src, "container-1", "container-2");
        if (getParameter("is-iframe") !== "true") {
            window.scrollTo({top: 0, behavior: "instant"});
        }
    }
}

const src = getParameter("src");
if (src) {
    window.currentArticle = {"realUrl": src};
    openFile(src);
} else {
    const fakeUrl = getParameter("fakeUrl");
    if (fakeUrl) {
        const realUrl = window.parseFakeUrl(fakeUrl, {fakeUrl});
        window.currentArticle = {"realUrl": realUrl, "fakeUrl": fakeUrl};
        openFile(realUrl);
    }
}
if (window.setPreviousAndNextButtonEnabledStatus && typeof (window.setPreviousAndNextButtonEnabledStatus) === "function") {
    window.setPreviousAndNextButtonEnabledStatus();
}
